CREATE TABLE IF NOT EXISTS `title_basic` (
    `id` int(11) unsigned DEFAULT NULL AUTO_INCREMENT,
    PRIMARY KEY (`id`)
);
CREATE TABLE IF NOT EXISTS `title_rating` (
    `id` int(11) unsigned DEFAULT NULL AUTO_INCREMENT,
    PRIMARY KEY (`id`),
	FOREIGN KEY(`primaryTitle`, `startYear`, `runtimeMinutes`, `genre`) REFERENCES `title_basic` ON DELETE NO ACTION ON UPDATE NO ACTION,
	FOREIGN KEY(`isAdult`, `averageRating`, `numVotes`) REFERENCES `title_basic` ON DELETE NO ACTION ON UPDATE NO ACTION
);
CREATE TABLE IF NOT EXISTS `name_basic` (
    `id` int(11) unsigned DEFAULT NULL AUTO_INCREMENT,
    PRIMARY KEY (`id`),
	FOREIGN KEY(`birthYear`, `deathYear`, `primaryProfession`, `knownForTitles`) REFERENCES `title_basic` ON DELETE NO ACTION ON UPDATE NO ACTION
);
CREATE TABLE IF NOT EXISTS `title_crew` (
    `id` int(11) unsigned DEFAULT NULL AUTO_INCREMENT,
    PRIMARY KEY (`id`),
	FOREIGN KEY(`director`) REFERENCES `name_basic` ON DELETE NO ACTION ON UPDATE NO ACTION,
	FOREIGN KEY(`writer`) REFERENCES `name_basic` ON DELETE NO ACTION ON UPDATE NO ACTION
);
CREATE TABLE IF NOT EXISTS `title_principal` (
