def readInput(filename):
    """Reads user's input from file."""
    lines = []

    # Read all content of text files into list of strings.
    if not filename == "":
        fh = open("input/{}".format(filename), encoding="utf-8")

        while True:
            line = fh.readline()

            if len(lines) >= 799: break

            elif line!= "" :  
                lines += [line]
    else:
        print("\nNo inputs found! Try again...\n")
        return None

    return lines[0:-1][::2], lines[-1:]

