        _ "github.com/jinzhu/gorm"
)
func main() {
	var title string = os.Args[1];
	var preference string = os.Args[2];
	db := getDB();
	stmt, err := db.Prepare("INSERT INTO history VALUES(?);")
	if err!= nil{
            fmt.Println(err)
            return ;
         }
	_, err = stmt.Exec(preference)
	if err!=nil {
                fmt.Println(err)
                return ;
             }
	stmt, err = db.Prepare(`INSERT INTO history (userid,titleid,rating) values ($1,$2,$3)`)
	if err!= nil {
                     fmt.Println(err)
                     return ;
              }
	result, err:= execQuery(stmt,"userid","titleid","rating");
	if err==nil && len(result)>=1 {
		for _,row := range result {
			fmt.Printf("%v %v %v \n ", row[0], row[1], row[2])
		}
	}else{
               fmt.Println(err)
          }
	stmt, err = db.Prepare("UPDATE ratings SET rating=$1 WHERE id=(select max(id) from ratings where title_id=$2)")
	if err == nil {
		result, err = execQuery(stmt, 3+float64(int(preference)), int(title))
		if err == nil {
			fmt.Println(len(result), result)
		}else{
		   fmt.Println(err)
		}
	}
	db.Close()
}
