package main
import "fmt"
// Create new structs from the above data
type Movie struct { // Movie Title Information
	MovieID    string   `json:"id"`     // Alphabetic ID
	Title      string   `json:"original_title"`         // Original title, in English
	Year       int      `json:"release_date"`           // Release date
	Genre      []string `json:"genres"`               // Genres
}

func NewMovie(title string, yr int, g []string) *Movie{
	return &Movie{
		MovieID:    fmt.Sprintf("%v-%v", len(g), title[:len(title)-len("The")]),
		Title:      title,
		ReleaseDate: yr + "-01-01",
		Genre:      g,
	}
}

var Movies = map[int]*Movie{}
for i := range Movies {
	Movies[i] = NewMovie(`The`, 2021, nil)
}
