SELECT * FROM (title_basic WHERE title_type = "movie" AND (start_year < 2008 OR start_year > 1967)) LEFT JOIN (name_basic ON (title_id=primary_title)) RIGHT OUTER JOIN (title_rating ON (title_id=primary_title)) INNER JOIN (title_crew ON (director_id IN (1))) RIGHT OUTER JOIN (title_principal ON (character_actor_id IN ('Tom Hanks'))) GROUP BY director_id ORDER BY COUNT(job) DESC LIMIT 1