SELECT DISTINCT t.title_id as id, r.average_rating as rating from imdb.movie AS m INNER JOIN imdb.title_ratings AS tr ON tr.title_id = m.imdb_id LEFT OUTER JOIN imdb.name_basics AS nb ON nb.primary_name = m.original_title LEFT OUTER JOIN imdb.title_crew AS c ON c.director = nb.primary_profession AND director IN ('James Cameron') WHERE ((m.genre LIKE '%Epic%') OR (m.category LIKE '%History%')) GROUP BY t.title_id ORDER BY rating DESC LIMIT 8