SELECT t.titleID AS TitleID, t.primaryTitle As PrimaryTitle, t.originalTitle as OriginalTitle, t.isAdult as IsAdult, t.startYear as Startyear, t.endYear as Endyear, t.runtimeMinutes as RuntimeMinutes, g.genreID as GenreID FROM title_basics t INNER JOIN title_ratings r ON t.titleID = r.titleID AND r.averageRating >= 8 WHERE t.titleID IN ('ForestGump','ShawshankRedmption') OR EXISTS( SELECT * from title_ratings where t.titleID in('ForestGump','ShawshankRedmption')) GROUP BY t.titleID HAVING COUNT(*) > 1