SELECT * FROM (title_basics tb INNER JOIN  title_ratings tr ON tb.primaryTitle = tr.primaryTitle AND tb.isAdult=tr.isAdult AND tb.startYear BETWEEN tr.startYear-10 AND tr.endYear+10 )INNER JOIN(name_basics nb INNER JOIN title_crew tc ON nb.primaryName=tc.director OR nb.primaryName=tc.writer OR nb.primaryName=tc.producer OR nb.primaryName=tc.actor OR nb.primaryName=tc.actress)ON tb.titleType='movie' WHERE nb.category='Actor' GROUP BY tb.titleType ORDER BY COUNT(*) DESC LIMIT 6