SELECT DISTINCT tb.* FROM (title_basics as tb INNER JOIN title_ratings AS tr ON tb.id = tr.movieId WHERE ((tr.averageRating >? AND tr.numVotes >=?)) OR ((tb.isAdult=?))) AND (((tb.startYear <=? )AND( tb.endYear>=?)OR((tb.startYear<=? AND bb.endYear>=?)))) AND (((tb.runtimeMinutes<=? AND tb.runtimeMinutes>?)OR((tb.runtimeMinutes<=? AND tb.runtimeMinutes>=?)))), (title_crew as tc INNER JOIN name_basics as nb ON tc.directorID = nb.personId) where ((tc.job LIKE '%Direct%' OR tc.job LIKE'%Writer %')AND (nb.primaryProfession='Actor')) AND (tb.genre IN ('Action','Comedy'))