SELECT t.titleID as MovieID, tb.titleType AS TitleType, tb.primaryTitle As PrimaryTitle, tr.averageRating as AverageRating, nb.name AS NameBasis, c.category as Category, j.job AS Job FROM title_ratings r JOIN title_basic b ON r.movieId = b.titleId LEFT OUTER JOIN name_basic nb ON b.primaryName=nb.name LEFT OUTER JOIN title_crew c ON nb.name =c.primaryName AND nb.name in ('Actor','Director') WHERE ((r.numvotes >= 100)) ORDER BY nb.name ASC