SELECT * FROM ( SELECT t.*, rr.* from (select title_basics.* where title_type = "Drama") as t inner join ( select avg(rating), count(*) as votes from ratings group by rating ) as rr ON t.title_id=rr.movie_id WHERE t.startyear >= 1980 AND t.endyear <= 2020 GROUP BY genre ) AS s WHERE NOT EXISTS ( SELECT title_principals.*, rating from title_principals INNER JOIN titles ON title_principals.title_id = titles.title_id AND (category='Lead Actor') OR (job='Supporting Actress')) ORDER BY rating DESC LIMIT 6