SELECT t.title_id as id, t.title_type as type, t.primary_title as mainmovie, t.original_title as altname, i.isadult as adult, t.startyear as year, t.endyear as year, t.runtimeminutes as length, g.genre_id as genreID, tc.averagerating as rating from title_basics AS t INNER JOIN title_ratings AS tr ON(tr.title_id=t.title_id ) inner join name_basics AS ni on (ni.title_id = t.title_id AND ni.birthyear <= now()) where NOT EXISTS (select * FROM name_basics WHERE title_id IN (select title_id from title_basics)) group by t.title_id order by rating desc LIMIT 1