SELECT t.* FROM title AS t WHERE 1=1 AND ((t.startyear <=? OR t.endyear >=?)) AND EXISTS ( SELECT * FROM title_ratings WHERE t.tconst = r.tconst ) AND EXISTS ( SELECT * FROM title_principals p WHERE p.job LIKE '%actor%' AND NOT EXISTS (SELECT COUNT(*) FROM title_principals pr WHERE pr.tconst = t.tconst AND pr.category!= 'character')) ORDER BY (averageRating+numvotes)/2 DESC LIMIT 10 OFFSET 0