SELECT DISTINCT title_basic.* FROM (title_rating INNER JOIN title_basics ON title_rating.primaryTitle = title_basics.primaryTitle) INNER JOIN (name_basics INNER JOIN title_basics ON name_basics.primaryTitle = title_basics.primaryTitle AND name_basics.birthYear >= 1980) ON ((name_basics.primaryName IN ('Chris Evans', 'Robert Downey Jr.'))) WHERE (((title_rating.numVotes > 6)) OR ((title_rating.averageRating < 7.5)) OR ((title_rating.genre LIKE '%thriller%'))) ORDER BY rating DESC LIMIT 5