SELECT tc.* FROM titles AS tc WHERE ((((SELECT COUNT(*) FROM rating AS rr WHERE rr.title_id = tc.title_id AND rr.rating >=.9)) > ((SELECT AVG(rr.rating) FROM ratings as r WHERE r.title_id=tc.title_id))) OR ((SELECT SUM(rr.rating) from ratings as r where r.title_id=tc.title_id)>=(SELECT AVERAGE(rr.rating)FROM ratings as r WHERE r.title_id=tc.title_id)))AND tc.genre IN ('Action','Comedy') GROUP BY tc.title_type ORDER by tc.startyear DESC LIMIT 1