SELECT DISTINCT TITLE_PRINCIPALS.* FROM TITLE_PRINCIPALS JOIN TITLE_CREW ON TITLE_CREW.TCONST = TITLE_PRINCIPALS.TCONST WHERE ((TITLE_CREW.DIRECTORS LIKE '%Will Ferrell%') AND (TITLE_CREW.WRITERS NOT IN ('%Matt Ember%'))) OR ((TITLE_CREW.WRITERS LIKE '%Will Ferrell%')) OR ((TITLE_CREW.DIRECTOR OF PHOTOGRAPHY LIKE '%Jonathan Brownlee%')) OR ((TITLE_CREW.EDITED BY LIKE '%Christopher Greenbury%'))