SELECT DISTINCT t.title AS MovieID, tb.titleType AS TitleType, tb.primaryTitle AS PrimaryTitle, tb.originalTitle AS OriginalTitle, tb.isAdult as Adult, tb.startYear as StartYear, tb.endYear as EndYear, tb.runtimeMinutes as RuntimeMinutes, g.genreNames AS GenreNames FROM title_basic t INNER JOIN title_rating tr ON t.title_id = tr.titleId AND tr.averageRating >= 7 WHERE t.primaryTitle LIKE '%Superhero%' OR tb.titleType IN ('Drama','Action') ORDER BY rating DESC LIMIT 10