SELECT DISTINCT t.titleId AS TITLEID FROM title_basics t JOIN title_ratings r ON r.titleId = t.titleId AND r.averageRating >= 6.7 WHERE t.isAdult = false AND ((r.numVotes > 8 OR r.avgRating < 6.7)) AND EXISTS( SELECT * FROM title_principals p WHERE p.job IN ('Actor') AND NOT EXISTS(SELECT COUNT(*) from title_principals p WHERE p.category!= '' AND p.job IS NULL AND p.character = '') ) ORDER BY t.startYear DESC