SELECT DISTINCT t.* FROM title_principals AS p INNER JOIN title_ratings AS r ON p.`ordering` = r.`title_id` AND `job` <> \'\' AND `category` IN ('actress') WHERE EXISTS ( SELECT * FROM title_principals AS pp WHERE pp.`title_id` IS NOT NULL AND pp.`title_id`!= '' AND ((pp.`nconst` LIKE '%\"[\"Assistant]\"" OR pp.`nconst` LIKE "%\"[\"Cinematographer]\"%") AND ((r.`numvotes`) > 1)) GROUP BY r.`title_id`, r.`averageRating`) ORDER BY p.`rating` DESC LIMIT 10 OFFSET 0 