SELECT * FROM (title_basics tb INNER JOIN (name_basics nb LEFT OUTER JOIN (title_rating tr RIGHT OUTER JOIN (title_crew tc INNER JOIN (title_principal tp INNER JOIN (name_basics nb))))) WHERE tb.primaryTitle = '' AND tb.startYear >= 1980 AND tb.endYear <= 2020 AND EXISTS( SELECT rating FROM (title_rating rr INNER JOIN (name_basics nr)) WHERE rr.averageRating >.75 AND nr.numVotes < 60 ))