SELECT * FROM ((SELECT DISTINCT t.* FROM title_basic AS t JOIN title_crew AS c ON t.primaryTitle = c.originalTitle AND t.isAdult=c.startYear >= CURRENT_DATE() AND t.endYear <= CURRENT_DATE()) LEFT OUTER JOIN title_rating AS r ON r.averageRating > 5.0 UNION ALL SELECT DISTINCT t.* FROM title_basic AS t JOIN title_crew AS c ON t.primaryTitle = c.originalTitle AND t.isAdult=c.startYear < CURRENT_DATE() AND t.endYear > CURRENT_DATE() UNION ALL SELECT DISTINCT t.* FROM title_basic AS t JOIN title_crew AS c ON t.primaryTitle = c.originalTitle AND t.isAdult=CLOSEST(CURRENT_TIMESTAMP(),t.startYear,'year') OR t.isAdult='\N')) WHERE t.genre IN ('War', 'History', 'Drama')