SELECT DISTINCT
title_basic.*, title_rating.*, name_basics.*, title_crew.*, title_principal.* FROM title_basic INNER JOIN title_rating ON title_basic.title_id = title_rating.title_id INNER JOIN name_basics ON title_basic.title_id = name_basics.title_id INNER JOIN title_crew ON title_basic.title_id=title_crew.title_id INNER JOIN title_principal ON title_basic.title_id = title_principal.title_id WHERE ((title_basic.primaryTitle LIKE '%Mary%') OR (title_basic.originalTitle LIKE '%Mary%')) AND (name_basics.birthYear >= 1976 )AND(name_basics.deathYear <= 2020) AND (title_basic.isAdult = true)) GROUP BY title_basic.title_id ORDER BY RANDOM() LIMIT 5