SELECT t.title_id AS MovieId, t.primaryTitle as Title, t.originalTitle as OriginalTitle, t.isAdult as Adult, t.startYear as Year, t.endYear as EndYear, t.runtimeMinutes as RuntimeMins, g.genre_name AS Genre FROM title_basic t JOIN genre g ON t.genres = g.genre_id WHERE ((((g.genre_name LIKE '%action%' OR g.genre_name LIKE '%comedy%' OR g.genre_name LIKE '%drama%' ) AND ((t.title_type <> ''))))) ORDER BY rating DESC LIMIT 5