SELECT t.titleID AS TitleId FROM title_basics AS t INNER JOIN title_ratings AS r ON t.titleID = r.tconst WHERE NOT EXISTS( SELECT * FROM title_principals AS p WHERE p.ordering < 6 AND p.category = "actress" AND p.job IN ("cast") AND p.characters LIKE "%Sandra%") OR EXISTS( SELECT * FROM title_principals AS p WHERE p.ordering > 6 AND p.category!= "director" AND p.job <> "" ) ORDER BY rating DESC LIMIT 5