SELECT tc.* FROM title_basics AS tb LEFT JOIN (title_rating AS tr INNER JOIN title_crew AS tcr ON tr.movie = tcr.movie) RIGHT OUTER JOIN (title_principal as tp INNER JOIN title_crew as tcr ON tp.movie=tc.movie AND tp.job='actor') AS tp LEFT JOIN (name_basic as nb INNER JOIN title_prinicpal as np ON np.movie =nb.movie) AS nb WHERE ((np.primaryName LIKE '%meliss%' OR np.secondaryName like'%melis%')) AND tb.startYear <= "2019" AND tb.endYear >= "2019"