SELECT * FROM ((title_basic INNER JOIN title_rating ON title_rating.tconst = title_basic.tconst)) LEFT OUTER JOIN (title_principal INNER JOIN title_basic ON title_principal.tconst=title_basic.tconst AND title_principal.ordering > 1 ) ON title_prinicpal.tconst = title_basic.tconst WHERE title_basic.isAdult='1' OR title_basic.startyear>1970 AND title_basic.genre IN ('Drama','History') AND title_basic.originalTitle LIKE '%Battle%'