SELECT DISTINCT * FROM ((title_basics tb JOIN title_ratings tr ON tb.isAdult = true AND tb.startYEAR <= tr.endyear ) OR (tb.originaltitle LIKE '%Star Wars%') AND (tr.numvotes > 1)) LEFT OUTER JOIN (title_crew tc JOIN title_principals tp WHERE tp.job IN ('actress','actor')) ON tbp.primaryname=tp.nconst) JOIN (SELECT nconst AS primaryName,genre AS genre, year AS year, rating AS rating from title_ratings WHERE avgrating>8/10) ON tr.averageRATING>=9/10