SELECT * FROM ( SELECT DISTINCT tconst, nconst AS name FROM title_basics WHERE nconst IN ('nm0000001','nm0005690') AND titleType='Movie' ) AS mainTable JOIN title_principals ON mainTable.tconst = title_principals.tconst JOIN title_crew ON title_principals.tconst=title_crew.tconst JOIN title_rating ON title_principals.tconst = title_rating.tconst WHERE titleType='Movie' AND mainTable.nconst LIKE '%Tom%Hanks%' OR mainTable.nconst LIKE '%Julia%Roberts%' GROUP BY tconst ORDER BY rating DESC LIMIT 2 OFFSET 0)