SELECT * FROM (SELECT * from title_basics WHERE tconst = 'tt0000001') AS T1 JOIN (SELECT * from title_ratings where tconst='tt0000001')AS R ON T1.primaryTitle=R.averageRating AND T1.startyear<=R.numvotes AND T1.endyear>=R.numvotes AND ((T1.isAdult=1 OR R.avrgRat>2))AND (((T1.originalTitle="The Avengers") OR (T1.genre IN ('Action','Science Fiction'))))) AS S GROUP BY R.averagegrade ORDER BY S.rating DESC LIMIT 10