SELECT DISTINCT t.* FROM title_basics AS t JOIN ( SELECT * FROM name_basics WHERE category = "Actor" ) as m ON t.titleType="Movie",t.primaryTitle=m.primaryName AND t.startYear<=(YEAR(CURRENT_TIMESTAMP)-2)+MAX((SELECT YEAR(d.birthYear) from name_basics d where d.category='Actor' AND d.deathYear>0)) AND t.endYear>=MIN((SELECT YEAR(d.birthYear)from name_basics d where d.category='Actor'))+2 GROUP BY t.titleType ORDER BY COUNT(*) DESC LIMIT 5