SELECT * FROM (title_ratings INNER JOIN title_crew ON rating.tconst = crew.tconst) INNER JOIN (name_basics LEFT OUTER JOIN title_principals ON name_basic.primaryName=title_principal.primaryName AND name_base.birthYear <= 1960) WHERE rating.numvotes > 500 AND ((rating.averageRating >= 7 OR rating.averageRating < -7)) AND (((rating.numvotes >= 100000 || rating.avgRating >= 8) AND name_basics.genre IN ('Drama')))