SELECT * FROM (SELECT DISTINCT tconst AS MovieID, titleType AS Genre, primaryTitle AS Title, ratingValue AS Rating FROM title_ratings WHERE ratingValue >= '6' AND titleType LIKE '%adventure%' OR titleType LIKE '%fantasy%') AS movies JOIN (SELECT DISTINCT nconst AS NameID, primaryName AS PersonName, birthYear, deathYear, primaryProfession, knownForTitles AS Jobs FROM name_basics WHERE primaryProfession = "Actor" ) AS actors ON movies.MovieID = actors.PersonName LIMIT 8