SELECT DISTINCT * FROM ((SELECT TOP 5 title_basics.*,(SELECT COUNT(*) AS `num_votes` FROM title_rating WHERE title_id = title_basics.`tconst`) AS VOTES FROM title_basics JOIN title_ratings ON title_basics.`tconst`=title_ratings.`tconst`) JOIN title_principal ON title_basic.`tconst`=`title_principal`.`tconst` AND `ordering` IN ('1','2') ORDER BY (`average_rating`,`startyear`) DESC LIMIT 0, 5)