SELECT DISTINCT t.* FROM imdb.title AS t INNER JOIN imdb.name_basic as b ON t.id = b.imdbrmovieID WHERE (b.primaryName LIKE '%Shutter%') AND (b.primaryName LIKE %'%Suspence%' OR b.primaryName LIKE "%Thrillers%" OR b.primaryName LIKE "%%") AND NOT EXISTS( SELECT * FROM imdb.title_principal WHERE imdbrmovieID IN (SELECT id from imdb.title where titleType='Movie')) ORDER BY RAND() LIMIT 1