SELECT * FROM (title_basics tb INNER JOIN title_ratings tr ON tb.primaryTitle = tr.primaryTitle WHERE tr.numVotes > 2 AND tr.averageRating >= 6/10) AS r INNER JOIN (name_basics n INNER JOIN title_crew tc ON n.primaryName LIKE CONCAT('%', tc.director)) AS d ON r.primaryTitle=d.primaryTitle INNER JOIN (name_basics np INNER JOIN title_principals tp ON np.category='Actor' OR np.job IN ('Actress')) AS p ON r.primaryTitle=tp.ordering WHERE tp.job NOT IN ('Director') ORDER BY d.startYear DESC LIMIT 3