SELECT * FROM (SELECT CONCAT('Movie ', TITLE_BASICS.titleType,' ',TITLE_PRINCIPALS.job,' ',TITLE_CREW.director,'s',TITLE_CREW.writer,' ',TITLE_RATINGS.averageRatings,'/10') AS MovieInfo FROM TITLE_BASICS JOIN TITLE_CREW ON TITLE_CREW.primaryTitle = TITLE_BASICS.titleType JOIN TITLE_PRINCIPALS ON TITLE_PRINCIPALS.ordering = TITLE_CREW.ordering WHERE ((TITLE_BASICS.startYear >=? AND TITLE_BASICS.endYear <=?) OR (TITLE_BASICS.startYear IS NULL AND TITLE_BASICS.endYear IS NULL)) AND (TITLE_BASICS.isAdult = FALSE)