SELECT * FROM (SELECT tb.*,(SELECT COUNT(*) FROM title_rating WHERE rating >= 7 AND rating <= 9 ) AS `rating`,(SELECT AVG(`averageRatng`)FROM title_rating WHERE rating>= 7 AND rating<=9)AS avgRating,(SELECT SUM((SELECT COUNT(*)FROM title_cast WHERE character IN ('Jamie Foxx', 'Liam Neeson'))))AS totalCasts FROM title_basicstb WHERE title_type = "movie" GROUP BY tb.title_id LIMIT 5)