SELECT DISTINCT titles.* FROM title_rating AS ratings INNER JOIN ( SELECT * FROM title_ratings WHERE rating >= '6' AND rating <= '8') as goodmovies ON titles.titleID = goodmovies.movieID INNER JOIN title_info AS info ON titles.titleID=info.titleID INNER JOIN (SELECT title_principal.characters FROM title_prinicpal WHERE type='actor/actress', ordering='descending', category='main cast') AS actors ON titles.titleID = actors.titleID INNER JOIN (SELECT CONCAT(name_basic.primaryName,' ',name_basic.birthYear) as name FROM name_basic WHERE CONVERT(year(deathYear),varchar(10)) IS NULL ORDER BY CONVERT(year(birthYear),varchar(10)))AS actors ON titles.titleID = actors.titleID