SELECT t.* FROM (title_basics AS t JOIN name_basics ON t.primaryTitle = n.primaryName) JOIN crew CROSS APPLY (select distinct c.director from title_crew WHERE title_id=t.titleID AND director=c.name) DISTINCT ON(t.titleID) t, name_basics N where (N.birthyear >= "1960" OR N.deathyear <= "2000") and (D.numVote > 5) and (D.averageRate >= 6/10) ORDER BY ABS((CAST(d.numVoters as FLOAT)) / CAST("6" as float))