SELECT t.title_id AS id, t.title_type AS type, t.primary_title as Title, t.original_title as Original, t.is_adult as Is Adult, t.start_year as Start Year, t.end_year as End Year, t.runtime_minutes as Runtime Minutes, g.genre_name AS Genre Name from title_basics t JOIN genre_list g ON t.genres = g.genre_id WHERE type='Drama' AND ((Start Year <= 2000) OR (End Year >= 2000)) GROUP BY t.title_id HAVING COUNT(DISTINCT t.title_id)>=2