SELECT t.titleID AS TitleID, t.titleType AS Type, t.primaryTitle AS PrimaryTitle, t.originalTitle AS OriginalTitle, t.isAdult AS IsAdult, t.startYear AS StartYear, t.endYear AS EndYear, t.runtimeMinutes AS RuntimeMinutes, g.genreID as GenreID FROM titles t INNER JOIN genre g ON t.genres = g.genreID WHERE userPref LIKE '%dramma%' AND userPref LIKE '%new%' ORDER BY rating DESC LIMIT 6