SELECT t.* FROM title_ratings AS r INNER JOIN title_principals AS p ON r.title = p.primaryTitle WHERE p.category IN ('Actor') AND EXISTS( SELECT * FROM title_crew as c INNER JOIN title_ratings as cr ON c.director = cr.averageDirectingRating > 7.8 UNION ALL SELECT * FROM title_crew as c INNER JOIN title_ratings as cr ON c.writer = cr.averageWritingRating < 6.9 ) ORDER BY avgRatings DESC LIMIT 1