SELECT * FROM (SELECT tc.*, nc.*, rc.* FROM title_basic AS tc INNER JOIN title_rating AS tr ON tc.titleId =tr.titleId LEFT OUTER JOIN name_basic as nc ON tc.movieId=nc.nameId INNER JOIN title_crew AS tcr ON tc.titleId = tcr.titleId LEFT OUTER JOIN title_principal as tpr ON tc.titleId = tpr.titleId WHERE tc.isadult=false AND nc.primaryName IN ('Chadwick Boseman','Danai Gurira') AND tcr.job IN('Director') AND tpr.category='Lead' AND nc.birthYear BETWEEN 1976 AND 1980 ) AS temp GROUP BY titleId HAVING COUNT(DISTINCT nc.primaryName)>1