SELECT t.* FROM (title_basic, title_rating, name_basic, title_crew, title_principal WHERE title_basic.primaryTitle = "The Dark Knight" AND title_basic.isAdult=false AND title_basic.startYear<1970 OR title_basic.endYear>1990 AND NOT EXISTS( SELECT * FROM title_prinicpal WHERE title_princical.characters="Batman") AND ((NOT EXISTS(SELECT *FROM title_rating WHERE title_rating.averageRating<=3))OR((NOT EXISTS(SELECT *FROM title_rating WHERE title_rating.averageRating>=5))))AND NOT EXISTS(SELECT *FROM name_basic WHERE name_basic.deathYear>2010) AND NOT EXISTS(SELECT *FROM title_crew WHERE directors="Christopher Nolan")) AS t GROUP BY t.title_basics.primaryTitle ORDER BY RANDOM() LIMIT 3