SELECT * FROM (SELECT tc.*, nb.*, tr.*, nc.*, c.name AS crewMemberName FROM tconst AS tc JOIN name_basic ns ON tc.titleId = ns.id JOIN title_rating tr ON ns.id = tr.movieId WHERE ((ns.primaryName LIKE '%good%') OR (ns.primaryName LIKE '%how%')) AND (tr.averageRating >=.7)) LEFT OUTER JOIN casts as cc ON tc.id=cc.titleId LEFT OUTER JOIN director d ON d.id=cc.directorID LEFT OUTER JOIN writer w ON w.id=cc.writerID GROUP BY tc.id, nb.id, tr.id, nc.job ORDER BY count(casts.id), count(writer.id))