SELECT * FROM ((title_basics INNER JOIN name_basics ON title_basics.primaryName = name_basics.primaryName) INNER JOIN title_principals ON title_basics.tconst=title_principals.tconst AND title_basics.startYear>=title_principals.ordering AND title_basics.endYear<=title_principals.ordering WHERE title_principals.category='actor')INNER JOIN title_rating ON title_basics.tconst=title_rating.tconst WHERE title_basics.isAdult=1 ORDER BY AVG(title_rating.averageRating)