SELECT * FROM ( SELECT DISTINCT TITLE_BASICS.*, TITLE_CREW.*, TITLE_PRINCIPALS.*, AVG(TITLE_RATINGS.AVERAGE_RATING)/COUNT(*) AS SCORE 
FROM TITLE_BASICS LEFT JOIN TITLE_CREW ON ((TITLE_BASICS.TCONST = TITLE_CREW.TCONST))LEFT JOIN TITLE_PRINCIPALS ON ((TITLE_BASICS.TCONST=TITLE_PRINCIPALS.TCONST AND TITLE_CREW.ORDERING = TITLE_PRINCIPALS.ORDERING))GROUP BY TITLE_BASICS.TCONST ORDER BY SCORE DESC LIMIT 5 ) WHERE TITLE_BASICS.ISADULT > 0 OR NOT EXISTS (SELECT COUNT(*) from TITLE_BASECS where ISADULT < 0)