SELECT DISTINCT t.* FROM title_ratings AS r INNER JOIN title_principals AS p ON p.job = 'actress' AND p.category IN ('character') AND r.rating > 5 WHERE NOT EXISTS ( SELECT * FROM title_ratings AS r1 INNER JOIN title_principals AS p1 ON p1.job = 'actor' AND p1.category IN ('character') AND r1.rating <= 5 ) GROUP BY t.tconst HAVING COUNT(*) >= 2 ORDER BY SUM((r.averageRating*r.numVotes)) DESC LIMIT 5