SELECT * FROM ( SELECT DISTINCT title_id AS id, title_type, CONCAT('[',job,']') AS job, category, CONCAT([primaryName],[category]) AS name, CONCAT([genre],'-',[runtimeMinutes]) AS genre, CONCAT(['Watch Now','Buy On Demand']) AS watchNow, CONCAT([ratingValue], '/', [numVotes]) AS rate, COUNT(*) AS views FROM title_ratings WHERE title_id = 'tt0000001' GROUP BY title_id, title_type, CONCAT('[',job,']'), category, CONCAT([primaryName],[category]), CONCAT(['genre'],'-',[runtimeMinutes]), CONCAT(['watch now','buy on demand']), CONCAT ([rate], '/ ', [numvotes])) AS rec ) AS r ON r.views >= 5 AND r.views <= 10 ORDER BY r.views DESC LIMIT 1