SELECT tb.* FROM titleratings AS r INNER JOIN title_basics as b ON b.titleID = r.titleID WHERE r.averageRating >.8 AND EXISTS( SELECT nc.nname FROM name_basics as n INNER JOIN title_principals as p ON n.nid=p.primaryName INNER JOIN np ON np.nid=p.category WHERE n.nid IN ('actress', 'actor') AND p.job='Acting' AND p.ordering>0 AND p.character IN('Lisa', 'Mike')) GROUP BY r.movieID ORDER BY COUNT(*) DESC LIMIT 3